    private void bsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsimpanActionPerformed
        // TODO add your handling code here:
         String  nik, nama, alamat, no_telepon, bagian;
        
        nik = tnik.getText();
        nama = tnama.getText();
        alamat = talamat.getText();
        no_telepon = ttelepon.getText(); 
        bagian = tbagian.getText();    
        
        if ((nama.equals(""))|(nik.equals(""))|(nama.equals(""))|(alamat.equals(""))|(no_telepon.equals(""))|(bagian.equals(""))){
            JOptionPane.showMessageDialog(null, "Pengisian Data Tidak Boleh Kosong");
            tnik.requestFocus();
        }else{
        String sql = "insert into data_pegawai values (?,?,?,?,?)";
        try{
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, tnik.getText());
            stat.setString(2, tnama.getText());
            stat.setString(3, talamat.getText());
            stat.setString(4, ttelepon.getText());
            stat.setString(5, tbagian.getText());
            

            stat.executeUpdate();
            JOptionPane.showMessageDialog(null,"Data berhasil disimpan");
            kosong();
            tnik.requestFocus();
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null,"Data gagal disimpan" +e);
        }
        datatable();
        }
    }//GEN-LAST:event_bsimpanActionPerformed

    private void tabeldatapegawaiMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabeldatapegawaiMouseClicked
        // TODO add your handling code here:
         int bar = tabeldatapegawai.getSelectedRow();
        String a = tabmode.getValueAt(bar, 0).toString();
        String b = tabmode.getValueAt(bar, 1).toString();
        String c = tabmode.getValueAt(bar, 2).toString();
        String d = tabmode.getValueAt(bar, 3).toString();
        String e = tabmode.getValueAt(bar, 4).toString();
        

        tnik.setText(a);
        tnama.setText(b);
        talamat.setText(c);
        ttelepon.setText(d);
        tbagian.setText(e);
        
        
        bsimpan.setEnabled(false);
    }//GEN-LAST:event_tabeldatapegawaiMouseClicked

    private void beditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_beditActionPerformed
        // TODO add your handling code here:
        try{
            String sql= "update data_pegawai set nik=?,nama=?,alamat=?,no_telepon=?,bagian=? where nik='"+ tnik.getText()+"'";
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1,  tnik.getText());
            stat.setString(2,  tnama.getText());
            stat.setString(3,  talamat.getText());
            stat.setString(4,  ttelepon.getText());
            stat.setString(5,  tbagian.getText());
            

            stat.executeUpdate();
            JOptionPane.showMessageDialog(null,"Data berhasil diubah");
            kosong();
             tnik.requestFocus();
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null,"Data gagal diubah" +e);
        }
        datatable();
        bsimpan.setEnabled(true);
    }//GEN-LAST:event_beditActionPerformed

    private void bhapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bhapusActionPerformed
        // TODO add your handling code here:
        int ok = JOptionPane.showConfirmDialog(null, "Yakin ingin menghapus data ini?","Konfirmasi dialog!",JOptionPane.YES_NO_OPTION);
        if (ok==0){
            String sql = "delete from data_pegawai where nik ='"+tnik.getText()+"'";
            try{
                PreparedStatement stat = conn.prepareStatement(sql);
                stat.executeUpdate();
                JOptionPane.showMessageDialog(null, "Data berhasil dihapus");
                kosong();
                tnik.requestFocus();
            }
            catch (SQLException e){
                JOptionPane.showMessageDialog(null, "Data gagal dihapus"+e);
            }
            datatable();
            bsimpan.setEnabled(true);
    }//GEN-LAST:event_bhapusActionPerformed
    }
