 protected void kosong(){
        tusername.setText("");
        tpassword.setText("*");
    }
    
    protected void login (){
       try {
            String sql = "SELECT * FROM login where username='"+tusername.getText()+"' and password='"+tpassword.getText()+"'";
            java.sql.Statement stat = conn.createStatement();
            ResultSet hasil = stat.executeQuery(sql);
            if (hasil.next()){
                System.out.println(hasil.getString("username"));
                if (tusername.getText().equals(hasil.getString("username")) && tpassword.getText().equals(hasil.getString("password"))){
                String sql1= "update login set status='on' where username='"+tusername.getText()+"'";
                PreparedStatement stat1 = conn.prepareStatement(sql1);
                stat1.executeUpdate();
                new menuutama().setVisible(true);
                this.setVisible(false);
                }
            } else {
                JOptionPane.showMessageDialog(null, "Username atau Password anda salah");
            }
        }catch (SQLException e){
            JOptionPane.showMessageDialog(this, e.getMessage());
            
        }
    }
