    private void tabeldatamobilMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabeldatamobilMouseClicked
        // TODO add your handling code here:
        int bar = tabeldatamobil.getSelectedRow();
        String a = tabmode.getValueAt(bar, 0).toString();
        String b = tabmode.getValueAt(bar, 1).toString();
        String c = tabmode.getValueAt(bar, 2).toString();
        String d = tabmode.getValueAt(bar, 3).toString();
        String e = tabmode.getValueAt(bar, 4).toString();
        String f = tabmode.getValueAt(bar, 5).toString();

    tplat.setText(a);
    tmerk.setText(b);
    jComboBox1.setSelectedItem(c);
    tharga.setText(d);
    ttahun.setText(e);
    tketerangan.setText(f);
    bsimpan.setEnabled(false);
    }//GEN-LAST:event_tabeldatamobilMouseClicked

