    private void bsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsimpanActionPerformed
        // TODO add your handling code here:
         String no_plat, merk, harga, tahun, keterangan;
        
    no_plat = tplat.getText();
    merk = tmerk.getText();
    harga = tharga.getText();
    tahun = ttahun.getText();
    keterangan = tketerangan.getText();
        
        if (no_plat.equals("")|(merk.equals(""))|(harga.equals(""))|(tahun.equals(""))|(keterangan.equals(""))){
            JOptionPane.showMessageDialog(null, "Pengisian Data Tidak Boleh Kosong");
            tplat.requestFocus();
        }else{
        String sql = "insert into data_mobil values (?,?,?,?,?,?)";
        try{
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, tplat.getText());
            stat.setString(2, tmerk.getText());
            stat.setString(3, jComboBox1.getSelectedItem().toString());
            stat.setString(4, tharga.getText());
            stat.setString(5, ttahun.getText());
            stat.setString(6, tketerangan.getText());
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null,"Data berhasil disimpan");
            kosong();
            tplat.requestFocus();
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null,"Data gagal disimpan" +e);
        }
        datatable();
        
    }            
    }//GEN-LAST:event_bsimpanActionPerformed

    private void beditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_beditActionPerformed
        // TODO add your handling code here:
          try{
            String sql= "update data_mobil set merk=?,tipe=?,harga=?,tahun=?,keterangan=? where no_plat='"+tplat.getText()+"'";
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, tmerk.getText());
            stat.setString(2, jComboBox1.getSelectedItem().toString());
            stat.setString(3, tharga.getText());
            stat.setString(4, ttahun.getText());
            stat.setString(5, tketerangan.getText());

            stat.executeUpdate();
            JOptionPane.showMessageDialog(null,"Data berhasil diubah");
            kosong();
            tplat.requestFocus();
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null,"Data gagal diubah" +e);
        }
        datatable();
         bsimpan.setEnabled(true);
    }//GEN-LAST:event_beditActionPerformed

