    private void bcariActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bcariActionPerformed
        // TODO add your handling code here:
        Object[] Baris ={"Nomor plat","Merk mobil","Tipe mobil","Harga Sewa","tahun","keterangan"};
    tabmode = new 
    DefaultTableModel(null, Baris);
    String cariitem = tcari.getText();
    
    try {
    String sql = "SELECT * FROM data_mobil where no_plat='"+cariitem+"' or merk='"+cariitem+"' "
            + "or tipe='"+cariitem+"' or harga ='"+cariitem+"' or tahun ='"+cariitem+"'or keterangan='"+cariitem+"'";
    Statement stat = conn.createStatement();
    ResultSet hasil = stat.executeQuery(sql);
    while (hasil.next()){
        tabmode.addRow(new Object[]{
        hasil.getString(1),
        hasil.getString(2),
        hasil.getString(3),
        hasil.getString(4),
        hasil.getString(5),
        hasil.getString(6)
        
    });
    }
    tabeldatamobil.setModel(tabmode);
    } catch (Exception e) {
    JOptionPane.showMessageDialog(null, "data gagal dipanggil"+e);
    }
    }//GEN-LAST:event_bcariActionPerformed

