     protected void kosong(){
    tnik.setText("");
    tnama.setText("");
    ttelepon.setText("");
    tplat.setText("");
    tmerk.setText("");
    ttipe.setText("");
    ttahun.setText("");
    jDate.setDate(null);
    
    }
     protected void autonumber(){
        String transaksi="";
        try{
        String sql = "SELECT no_transaksi FROM mobilkeluar order by no_transaksi asc";
        PreparedStatement stat = conn.prepareStatement(sql);
        ResultSet rs=stat.executeQuery(sql);
        while(rs.next()){
        transaksi=rs.getString("no_transaksi");
        }
        }catch(SQLException sqle){transaksi="";}  
        if(transaksi.length() <1){transaksi="TRK0000";}
        String ur=transaksi.substring(3);
        int u=Integer.parseInt(ur)+1;
        System.out.println(ur+"=="+u);
        if(u<10)
        {ur="000"+u;}
        else if(u<100)
        {ur="00"+u;}
        else if(u<1000)
        {ur="0"+u;}
        else 
        {ur=""+u;}
        transaksi  ="TRK"+ur;      
        ttransaksi.setText(transaksi);
        }
      
       private void datatable() {
    Object[] Baris ={"No Transaksi","NIK Pegawai","Nama Pegawai","Nomor Telepon","Nomor Plat","Merk Mobil","Tipe","Tahun","Tanggal"};
    tabmode = new 
    DefaultTableModel(null, Baris);
    
    try {
    String sql = "SELECT * FROM mobilkeluar order by no_transaksi";
    Statement stat = conn.createStatement();
    ResultSet hasil = stat.executeQuery(sql);
    while (hasil.next()){
        tabmode.addRow(new Object[]{
        hasil.getString(1),
        hasil.getString(2),
        hasil.getString(3),
        hasil.getString(4),
        hasil.getString(5),
        hasil.getString(6),
        hasil.getString(7),
        hasil.getString(8),
        hasil.getString(9)
      
    });
    }
    tabelmobilkeluar.setModel(tabmode);
    } catch (Exception e) {
    JOptionPane.showMessageDialog(null, "data gagal dipanggil"+e);
    }
    }
       
    
