    private void beditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_beditActionPerformed
        // TODO add your handling code here:
        try{
            String sql= "update mobilkeluar set nik=?,nama=?,no_telepon=?,no_plat=?,merk=?,tipe=?,tahun=?,tanggal=? where no_transaksi='"+ttransaksi.getText()+"'";
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, tnik.getText());
            stat.setString(2, tnama.getText());
            stat.setString(3, ttelepon.getText());
            stat.setString(4, tplat.getText());
            stat.setString(5, tmerk.getText());
            stat.setString(6, ttipe.getText());
            stat.setString(7, ttahun.getText());
            SimpleDateFormat dateFormat = new SimpleDateFormat ("dd-MM-yyyy");
            String tgl = dateFormat.format(jDate.getDate());
            stat.setString(8, tgl);
          
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null,"Data berhasil diubah");
            kosong();
            tplat.requestFocus();
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null,"Data gagal diubah" +e);
        }
        datatable();
        bsimpan.setEnabled(true);
        autonumber();
    }//GEN-LAST:event_beditActionPerformed

    private void tabelmobilkeluarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabelmobilkeluarMouseClicked
        // TODO add your handling code here:
         try{
            DefaultTableModel model = (DefaultTableModel)tabelmobilkeluar.getModel();
            int bar = tabelmobilkeluar.getSelectedRow();

            java.util.Date tgl = new SimpleDateFormat("dd-MM-yyyy").parse((String)model.getValueAt(bar, 8));
            jDate.setDate(tgl);
            String a = tabmode.getValueAt(bar, 0).toString();
            String b = tabmode.getValueAt(bar, 1).toString();
            String c = tabmode.getValueAt(bar, 2).toString();
            String d = tabmode.getValueAt(bar, 3).toString();
            String e = tabmode.getValueAt(bar, 4).toString();
            String f = tabmode.getValueAt(bar, 5).toString();
            String g = tabmode.getValueAt(bar, 6).toString();
            String h = tabmode.getValueAt(bar, 7).toString();
            String i = tabmode.getValueAt(bar, 8).toString();
           
            
            ttransaksi.setText(a);
            tnik.setText(b);
            tnama.setText(c);
            ttelepon.setText(d);
            tplat.setText(e);
            tmerk.setText(f);
            ttipe.setText(g);
            ttahun.setText(h);
            jDate.getDate();
           
            
            
        } catch (ParseException ex) {
            Logger.getLogger(formuser.class.getName()).log(Level.SEVERE, null, ex);
        }
        bsimpan.setEnabled(false);
    }//GEN-LAST:event_tabelmobilkeluarMouseClicked

