    private void bsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsimpanActionPerformed
        // TODO add your handling code here:
        String jumlah, total;
        jumlah = tsewa.getText();
        total = ttotal.getText();
        
        
        if (jumlah.equals("")|(total.equals(""))){
            JOptionPane.showMessageDialog(null, "Pengisian Data Tidak Boleh Kosong");
            tplat.requestFocus();
        }else{
        String sql = "insert into mobilkembali values (?,?,?,?,?,?,?,?,?)";
        try{
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, ttransaksi.getText());
            stat.setString(2, tplat.getText());
            stat.setString(3, tmerk.getText());
            stat.setString(4, ttipe.getText());
            stat.setString(5, ttahun.getText());
            SimpleDateFormat dateFormat = new SimpleDateFormat ("dd-MM-yyyy");
                    String tgl = dateFormat.format(jDate.getDate());
            stat.setString(6, tgl);
            stat.setString(7, tharga.getText());
            stat.setString(8, tsewa.getText());
            stat.setString(9, ttotal.getText());
           
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null,"Data berhasil disimpan");
            kosong();
            tplat.requestFocus();
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null,"Data gagal disimpan" +e);
        }
        datatable();
        
        }
    }//GEN-LAST:event_bsimpanActionPerformed

    private void tabelmobilkembaliMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabelmobilkembaliMouseClicked
        // TODO add your handling code here:
        try{
        DefaultTableModel model = (DefaultTableModel)tabelmobilkembali.getModel();
            int bar = tabelmobilkembali.getSelectedRow();

            java.util.Date tgl = new SimpleDateFormat("dd-MM-yyyy").parse((String)model.getValueAt(bar, 5));
            jDate.setDate(tgl);
        String a = tabmode.getValueAt(bar, 0).toString();
        String b = tabmode.getValueAt(bar, 1).toString();
        String c = tabmode.getValueAt(bar, 2).toString();
        String d = tabmode.getValueAt(bar, 3).toString();
        String e = tabmode.getValueAt(bar, 4).toString();
        String f = tabmode.getValueAt(bar, 5).toString();
        String g = tabmode.getValueAt(bar, 6).toString();
        String h = tabmode.getValueAt(bar, 7).toString();
        String i = tabmode.getValueAt(bar, 8).toString();
        
        ttransaksi.setText(a);
        tplat.setText(b);
        tmerk.setText(c);
        ttipe.setText(d);
        ttahun.setText(e);
        jDate.getDate();
        tharga.setText(g);
        tsewa.setText(h);
        ttotal.setText(i);
        
        } catch (ParseException ex) {
            Logger.getLogger(formuser.class.getName()).log(Level.SEVERE, null, ex);
        }
        bsimpan.setEnabled(false);
    }//GEN-LAST:event_tabelmobilkembaliMouseClicked

