    private void beditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_beditActionPerformed
        // TODO add your handling code here:
         try{
            String sql= "update mobilkembali set no_plat=?, merk=?,tipe=?,tanggal=?,tahun=?,harga=?,jumlah=?,total=? where no_transaksi='"+ttransaksi.getText()+"'";
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, tplat.getText());
            stat.setString(2, tmerk.getText());
            stat.setString(3, ttipe.getText());
            SimpleDateFormat dateFormat = new SimpleDateFormat ("dd-MM-yyyy");
            String tgl = dateFormat.format(jDate.getDate());
            stat.setString(4, tgl);
            stat.setString(5, ttahun.getText());
            stat.setString(6, tharga.getText());
            stat.setString(7, tsewa.getText());
            stat.setString(8, ttotal.getText());
            
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null,"Data berhasil diubah");
            kosong();
            tplat.requestFocus();
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null,"Data gagal diubah" +e);
        }
        datatable();
        bsimpan.setEnabled(true);
        
    }//GEN-LAST:event_beditActionPerformed

