    protected void kosong(){
    tusername.setText("");
    tnama.setText("");
    tpassword.setText("");
    buttonGroup1.clearSelection();
    ttempat.setText("");
    jDate.setDate(null);
    talamat.setText("");
    }
    protected void datatable(){
          Object[] Baris ={"Username","Password","Nama","Jenis Kelamin","Tempat Lahir","Tanggal Lahir","Alamat"};
          tabmode = new DefaultTableModel(null, Baris);
          tabelregister.setModel(tabmode);
    String sql = "SELECT * FROM login";
    try {
    Statement stat = conn.createStatement();
    ResultSet hasil = stat.executeQuery(sql);
    while (hasil.next()){
        tabmode.addRow(new Object[]{
        hasil.getString(1),
        hasil.getString(2),
        hasil.getString(3),
        hasil.getString(4),
        hasil.getString(5),
        hasil.getString(6),
        hasil.getString(7)
    });
    }
    tabelregister.setModel(tabmode);
    } catch (Exception e) {
    JOptionPane.showMessageDialog(null, "data gagal dipanggil"+e);
    }
      }
    
