    private void tabelregisterMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabelregisterMouseClicked
        // TODO add your handling code here:
         try {
            DefaultTableModel model = (DefaultTableModel)tabelregister.getModel();
            int bar = tabelregister.getSelectedRow();

            java.util.Date tgl = new SimpleDateFormat("yyy-MM-dd").parse((String)model.getValueAt(bar, 5));
            jDate.setDate(tgl);
            String a = tabmode.getValueAt(bar, 0).toString();
            String b = tabmode.getValueAt(bar, 1).toString();
            String c = tabmode.getValueAt(bar, 2).toString();
            String d = tabmode.getValueAt(bar, 3).toString();
            String e = tabmode.getValueAt(bar, 4).toString();
            String f = tabmode.getValueAt(bar, 5).toString();
            String g = tabmode.getValueAt(bar, 6).toString();

             tusername.setText(a);
             tpassword.setText(b);
             tnama.setText(c);
            if("Laki-laki".equals(d)){
                rl.setSelected(true);
            }
            else{
                rp.setSelected(true);
            }
            ttempat.setText(e);
            jDate.getDate();
            talamat.setText(g);
        } catch (ParseException ex) {
            Logger.getLogger(formuser.class.getName()).log(Level.SEVERE, null, ex);
            
        }
        bsimpan.setEnabled(false);             
    }//GEN-LAST:event_tabelregisterMouseClicked

