    private void bsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsimpanActionPerformed
        // TODO add your handling code here:
        String username, password, nama, tempat, alamat;
        username = tusername.getText();
        password = tpassword.getText();
        nama = tnama.getText();
        tempat = ttempat.getText();
        alamat = talamat.getText();

        if (username.equals("")|(password.equals(""))|(nama.equals(""))|(tempat.equals(""))|(alamat.equals(""))){
            JOptionPane.showMessageDialog(null, "Pengisian Data Tidak Boleh Kosong");
            tusername.requestFocus();
        }else{
            try{
                String sql = "insert into login values(?,?,?,?,?,?,?,?)";
                PreparedStatement stat = conn.prepareStatement(sql);
                try{
                    stat.setString(1, tusername.getText());
                    stat.setString(2, tpassword.getText());
                    stat.setString(3, tnama.getText());
                    String jenis = null;
                    if(rl.isSelected()){
                        jenis = "Laki-laki";
                    }else if(rp.isSelected()){
                        jenis = "Perempuan";
                    }
                    stat.setString(4, jenis);
                    stat.setString(5, ttempat.getText());
                    SimpleDateFormat dateFormat = new SimpleDateFormat ("yyy-MM-dd");
                    String tgl = dateFormat.format(jDate.getDate());
                    stat.setString(6, tgl);
                    stat.setString(7, talamat.getText());
                    stat.setString(8, String.valueOf(0));
                    stat.executeUpdate();
                    JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");

                }catch (SQLException e){
                    System.out.println("Data Gagal Disimpan" +e);
                    JOptionPane.showMessageDialog(null, "Data yang Anda masukkan salah" +e);
                }
            }catch(Exception e){
            }
        }

        datatable();
    }//GEN-LAST:event_bsimpanActionPerformed

