    private void bhapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bhapusActionPerformed
        // TODO add your handling code here:
         int ok = JOptionPane.showConfirmDialog(null, "Yakin ingin menghapus data user ini?","Konfirmasi dialog!",JOptionPane.YES_NO_OPTION);
        if (ok==0){
            String sql = "delete from login where username ='"+tusername.getText()+"'";
            try{
                PreparedStatement stat = conn.prepareStatement(sql);
                stat.executeUpdate();
                JOptionPane.showMessageDialog(null, "Data berhasil dihapus");
                kosong();
                tpassword.requestFocus();
            }
            catch (SQLException e){
                JOptionPane.showMessageDialog(null, "Data gagal dihapus"+e);
            }
            datatable();
            bsimpan.setEnabled(true);
        }
    }//GEN-LAST:event_bhapusActionPerformed

    private void beditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_beditActionPerformed
        // TODO add your handling code here:
         try{
            String sql = "update login set password=?, nama=?, jenis_kelamin=?, tempat_lahir=?, tanggal_lahir=?, alamat=?, status=? where username='"+tusername.getText()+"'";
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, tpassword.getText());
            stat.setString(2, tnama.getText());
            String jenis = null;
            if(rl.isSelected()){
                jenis = "Laki-laki";
            }else if(rp.isSelected()){
                jenis = "Perempuan";
            }
            stat.setString(3, jenis);
            stat.setString(4, ttempat.getText());
            SimpleDateFormat dateFormat = new SimpleDateFormat ("dd-MM-yyyy");
            String tgl = dateFormat.format(jDate.getDate());
            stat.setString(5, tgl);
            stat.setString(6, talamat.getText());
            stat.setString(7, String.valueOf(0));
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Diubah");
            kosong();
            tusername.requestFocus();

        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal diubah"+e);

        }
        datatable();
        bsimpan.setEnabled(true);
    }//GEN-LAST:event_beditActionPerformed

