    private void bcariActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bcariActionPerformed
        // TODO add your handling code here:
         Object[] Baris ={"Nomor PLAT","Merk Mobil","Tipe mobil","Harga Sewa","Tahun","Keterangan"};
    tabmode = new 
    DefaultTableModel(null, Baris);
    String cariitem = tcari.getText();
    
    try {
    String sql = "SELECT * FROM data_mobil where no_plat='"+cariitem+"' or merk='"+cariitem+"' "
            + "or tipe='"+cariitem+"' or harga ='"+cariitem+"' or tahun ='"+cariitem+"'or keterangan='"+cariitem+"'";
    Statement stat = conn.createStatement();
    ResultSet hasil = stat.executeQuery(sql);
    while (hasil.next()){
        tabmode.addRow(new Object[]{
        hasil.getString(1),
        hasil.getString(2),
        hasil.getString(3),
        hasil.getString(4),
        hasil.getString(5),
        hasil.getString(6)
    });
    }
    tabelpopupmobil.setModel(tabmode);
    } catch (Exception e) {
    JOptionPane.showMessageDialog(null, "data gagal dipanggil"+e);
    }
    }//GEN-LAST:event_bcariActionPerformed

    private void tabelpopupmobilMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabelpopupmobilMouseClicked
        // TODO add your handling code here:
        int tabelpopup = tabelpopupmobil.getSelectedRow();
        mbl.no_plat = tabelpopupmobil.getValueAt(tabelpopup, 0).toString();
        mbl.tipe = tabelpopupmobil.getValueAt(tabelpopup, 2).toString();
        mbl.harga = tabelpopupmobil.getValueAt(tabelpopup, 3).toString();
        mbl.itemTerpilih2();
        this.dispose();
    }//GEN-LAST:event_tabelpopupmobilMouseClicked

