    private void bcariActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bcariActionPerformed
        // TODO add your handling code here:
         Object[] Baris ={"NIK Petugas","Nama Petugas","Alamat","No Telepon","bagian"};
    tabmode = new 
    DefaultTableModel(null, Baris);
    String cariitem = tcari.getText();
    
    try {
    String sql = "SELECT * FROM data_pegawai where nik='"+cariitem+"' or nama='"+cariitem+"' "
            + "or alamat='"+cariitem+"' or no_telepon ='"+cariitem+"' or bagian='"+cariitem+"'";;
    Statement stat = conn.createStatement();
    ResultSet hasil = stat.executeQuery(sql);
    while (hasil.next()){
        tabmode.addRow(new Object[]{
        hasil.getString(1),
        hasil.getString(2),
        hasil.getString(3),
        hasil.getString(4),
        hasil.getString(5)
        
    });
    }
    tabelpopuppegawai.setModel(tabmode);
    } catch (Exception e) {
    JOptionPane.showMessageDialog(null, "data gagal dipanggil"+e);
    }
    }//GEN-LAST:event_bcariActionPerformed

    private void tabelpopuppegawaiMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabelpopuppegawaiMouseClicked
        // TODO add your handling code here:
         int tabelpopup = tabelpopuppegawai.getSelectedRow();
        sup.nik = tabelpopuppegawai.getValueAt(tabelpopup, 0).toString();
        sup.nama = tabelpopuppegawai.getValueAt(tabelpopup, 1).toString();
        sup.no_telepon = tabelpopuppegawai.getValueAt(tabelpopup,3).toString();
        sup.itemTerpilih();
        this.dispose();
    }//GEN-LAST:event_tabelpopuppegawaiMouseClicked

